import React,{useState} from 'react'

export default function TextForm(props) {

  const handleUpClick = () =>{     //This is an arrow function
    let newText = text.toUpperCase();
    setText(newText);
  }

  const handleOnChange = (event) =>{    //This is an arrow function
    setText(event.target.value);  //update text to whatever currently is in the text box
  }

  const [text, setText] = useState('Enter text here');  //creating a state, we use this since rect doesn't allows us to update a variables value easilt as text="new text";
  // text="new value of state" //wrong way to change state
  //setText("new Text");      //correct way to change state
  
  return (
    <div>
      <h1>{props.heading}</h1>
      <div className='mb-3'>
        <textarea className='form-control' value={text} onChange={handleOnChange} id='exampleformcontroltextarea1' rows="8"></textarea>  {/*this onChange function is a must or else we wont be able to type in the textbox*/}
      </div>
      <button className="btn btn-primary" onClick={handleUpClick}>Convert to UpperCase</button>
    </div>
  )
}
